import React, { useState } from "react";
import Wrapper from "./components/Wrapper";
import Screen from "./components/Screen";
import ButtonBox from "./components/ButtonBox";
import Button from "./components/Button";

const btnValues = [
  ["C", "DEL", "+-", "/"],
  [7, 8, 9, "*"],
  [4, 5, 6, "-"],
  [1, 2, 3, "+"],
  [0, ".", "%", "="],
];

const toLocaleString = (num) =>
  String(num).replace(/(?<!\..*)(\d)(?=(?:\d{3})+(?:\.|$))/g, "$1 ");

const removeSpaces = (num) => num.toString().replace(/\s/g, "");

const App = () => {
  const [calc, setCalc] = useState({
    sign: "",
    num: "0",
    res: "0",
  });

  const numClickHandler = (e) => {
    e.preventDefault();
    const value = e.target.innerHTML;

    if (removeSpaces(calc.num).length < 16) {
      setCalc((prevCalc) => ({
        ...prevCalc,
        num:
          prevCalc.num === "0" && value === "0"
            ? "0"
            : removeSpaces(prevCalc.num) % 1 === 0
            ? toLocaleString(Number(removeSpaces(prevCalc.num + value)))
            : toLocaleString(prevCalc.num + value),
        res: !prevCalc.sign ? "0" : prevCalc.res,
      }));
    }
  };

  const commaClickHandler = (e) => {
    e.preventDefault();
    const value = e.target.innerHTML;

    setCalc((prevCalc) => ({
      ...prevCalc,
      num: !prevCalc.num.toString().includes(".") ? prevCalc.num + value : prevCalc.num,
    }));
  };

  const signClickHandler = (e) => {
    e.preventDefault();
    const value = e.target.innerHTML;

    setCalc((prevCalc) => ({
      ...prevCalc,
      sign: value,
      res: !prevCalc.res && prevCalc.num ? prevCalc.num : prevCalc.res,
      num: "0",
    }));
  };

  const equalsClickHandler = () => {
    if (calc.sign && calc.num) {
      const math = (a, b, sign) =>
        sign === "+"
          ? a + b
          : sign === "-"
          ? a - b
          : sign === "*"
          ? a * b
          : a / b;

      let result;
      try {
        result = math(
          Number(removeSpaces(calc.res)),
          Number(removeSpaces(calc.num)),
          calc.sign
        );
        if (!isFinite(result)) throw new Error("Infinity");
        setCalc({
          ...calc,
          res: toLocaleString(result),
          sign: "",
          num: "0",
        });
      } catch (error) {
        setCalc({
          ...calc,
          res: "Error",
          sign: "",
          num: "0",
        });
      }
    }
  };

  const invertClickHandler = () => {
    setCalc((prevCalc) => ({
      ...prevCalc,
      num: toLocaleString(removeSpaces(prevCalc.num) * -1),
      res: toLocaleString(removeSpaces(prevCalc.res) * -1),
      sign: "",
    }));
  };

  const percentClickHandler = () => {
    let num = parseFloat(removeSpaces(calc.num));
    let res = parseFloat(removeSpaces(calc.res));

    setCalc((prevCalc) => ({
      ...prevCalc,
      num: toLocaleString(num / Math.pow(100, 1)),
      res: toLocaleString(res / Math.pow(100, 1)),
      sign: "",
    }));
  };

  const resetClickHandler = () => {
    setCalc({
      ...calc,
      sign: "",
      num: "0",
      res: "0",
    });
  };

  const backClickHandler = () => {
    setCalc((prevCalc) => ({
      ...prevCalc,
      num: prevCalc.num.toString().slice(0, -1) || "0",
    }));
  };

  return (
    <Wrapper>
      <Screen value={calc.num ? calc.num : calc.res} />
      <ButtonBox>
        {btnValues.flat().map((btn, i) => (
          <Button
            key={i}
            className={btn === "=" ? "equals" : ""}
            value={btn}
            onClick={
              btn === "C"
                ? resetClickHandler
                : btn === "DEL"
                ? backClickHandler
                : btn === "+-"
                ? invertClickHandler
                : btn === "%"
                ? percentClickHandler
                : btn === "="
                ? equalsClickHandler
                : btn === "/" || btn === "*" || btn === "-" || btn === "+"
                ? signClickHandler
                : btn === "."
                ? commaClickHandler
                : numClickHandler
            }
          />
        ))}
      </ButtonBox>
    </Wrapper>
  );
};

export default App;
