import React from 'react';
import Textfit from 'react-textfit';
import './Screen.css';

const Screen = ({ value }) => {
  return (
    <div className="screen">
      <Textfit mode="single" className="textfit">
        {value}
      </Textfit>
    </div>
  );
};

export default Screen;
